// ItemDetails.jsx
import React, { useState } from "react";
import "./ProductDisplay.scss";

const ItemDetails = ({ item }) => {
  const [selectedWeight, setSelectedWeight] = useState(item.weights[0]);

  return (
    <div className="item-details">
      <h1>{item.companyName}</h1>

      <div className="product">
        {/* Product Images Section */}
        <div className="product-images">
          <img className="main-image" src={item.images[0]} alt={item.name} />
          <div className="image-thumbnails">
            {item.images.map((img, index) => (
              <img
                key={index}
                src={img}
                alt={`${item.name} thumbnail ${index}`}
                onClick={() => setSelectedWeight(img)}
              />
            ))}
          </div>
        </div>

        {/* Product Info Section */}
        <div className="product-info">
          <h2>{item.name}</h2>
          <p className="price">Price: ${item.price}</p>
          <p className="stock">In Stock: {item.inStock ? "Yes" : "No"}</p>

          {/* Weight Selection */}
          <div className="weight-selection">
            <p>Weight:</p>
            <div className="weights">
              {item.weights.map((weight, index) => (
                <button
                  key={index}
                  className={selectedWeight === weight ? "active" : ""}
                  onClick={() => setSelectedWeight(weight)}
                >
                  {weight} lbs
                </button>
              ))}
            </div>
          </div>

          {/* Buttons */}
          <div className="actions">
            <button className="add-to-cart">Add to cart</button>
            <button className="buy-now">Buy now</button>
          </div>
        </div>
      </div>

      {/* Additional Info */}
      <div className="additional-info">
        <h3>About this item:</h3>
        <p>{item.description}</p>
      </div>
    </div>
  );
};

export default ItemDetails;
